void tableRest(List<SearchDto> result )
{
   Object[][] arr = new Object[result.size()][2];
    for(int i=0; i<result.size(); i++)
    {
            arr[i][0] = result.get(i).getType();
            arr[i][1] = result.get(i).getSearch_value();
        }
        sResult.setModel(new javax.swing.table.DefaultTableModel(arr, new String[] {
                                                                    "Type ", "Property" }));
    
    }
